mapper.select:
select d, ifnull(oiv.pay, 0) pay, ifnull(oiv.cnt, 0) cnt, ifnull(oiv.quantity, 0) quantity from date_t d left join (select date_format(regdate, '%Y-%m-%d') day, sum(totalprice) pay, count(oino) cnt, sum(quantity) quantity from orderitemvsettle where itemstate ='배송완료'
and cateid like '%${cateid}%'
and ${schKind} = #{keyword}
group by date_format(regdate, '%Y-%m-%d')) oiv on d.d = oiv.day where d between #{start} and #{end} order by d desc;

select date_format(d,'%Y-%m') d, ifnull(oiv.pay, 0) pay, ifnull(oiv.cnt, 0) cnt, ifnull(oiv.quantity, 0) quantity from date_t d left join (select date_format(regdate, '%Y-%m') day, sum(totalprice) pay, count(oino) cnt, sum(quantity) quantity from orderitemvsettle where itemstate ='배송완료'
and cateid like '%${cateid}%'
and ${schKind} = #{keyword}
group by date_format(regdate, '%Y-%m')) oiv on date_format(d,'%Y-%m') = oiv.day group by date_format(d,'%Y-%m') having d between #{start} and #{end} order by d desc;

select date_format(d,'%Y') d, ifnull(oiv.pay, 0) pay, ifnull(oiv.cnt, 0) cnt, ifnull(oiv.quantity, 0) quantity from date_t d left join (select date_format(regdate, '%Y') day, sum(totalprice) pay, count(oino) cnt, sum(quantity) quantity from orderitemvsettle where itemstate ='배송완료'
and cateid like '%${cateid}%'
and ${schKind} = #{keyword}
group by date_format(regdate, '%Y')) oiv on date_format(d,'%Y') = oiv.day group by date_format(d,'%Y') having d between #{start} and #{end} order by d desc;
